import { useForm } from "react-hook-form";

export const AdminCreateProductForm = () => {
  const { register, handleSubmit } = useForm();
  const onSubmit = (data) => console.log(data);
  return (
    <div className="admin-create-form">
      <form onSubmit={handleSubmit(onSubmit)}>
        <label htmlFor="name">Name of Product</label>
        <input id="name" {...register("name")} />

        <label htmlFor="description">Description of Product</label>
        <input id="description" {...register("description")} />

        <label htmlFor="price">Price</label>
        <input
          id="price"
          type="number"
          min={"0"}
          step={".01"}
          {...register("price")}
        />

        <label htmlFor="image">Select Image to Display</label>
        <input id="image" type="file" {...register("image")} />

        <label htmlFor="type">Type of Product</label>
        <select id="type" {...register("type")}>
          <option value="deck">Complete Deck</option>
          <option value="park">Complete Park</option>
          <option value="accessories">Deck Accessories</option>
          <option value="pieces">Park Piece</option>
        </select>
        <input type="submit" />
      </form>
    </div>
  );
};
