export const ProductView = ({ product }) => {
  console.log("PRODUCT IN PRODUCT VIEW", product);
  return (
    <div className="product-view-card-container">
      <img className="product-view-card-image" src={product.image}></img>
      <p className="product-view-card-name">{product.name}</p>
      <p className="product-view-card-description">{product.description}</p>
      <p className="product-view-card-price">Price : ${product.price}</p>
    </div>
  );
};
